/* eslint-disable no-unused-labels */
import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import Notification from './components/Notification'
import Error from './components/Error'
import LoginForm from './components/LoginForm'
import AddBlogForm from './components/AddBlogForm'
import Togglable from './components/Togglable'
import { useUsername, usePassword } from './hooks'
import { initBlogs, addNewBlog, likeBlog, removeBlog } from './reducers/blogReducer'
import { handleNotification } from './reducers/notificationReducer'
import { setUser, removeUser } from './reducers/userReducer'
import { handleError } from './reducers/errorReducer'
import { connect } from 'react-redux'
import loginService from './services/login'
import blogService from './services/blogs'
import {BrowserRouter as Router, Route, Link, Redirect, withRouter} from 'react-router-dom'
import UserInformation from './components/UserInformation'
import Home from './components/Home'
const App = ( props ) => {

    const { resetusername, ...username } = useUsername('username')
    const { resetpasswd, ...password } = usePassword('password')
    const [user, setUser] = useState(null)
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')
    const [url, setUrl] = useState('')

    const padding = {padding : 5}

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
        if (loggedUserJSON) {
            const user = JSON.parse(loggedUserJSON)
            setUser(user)
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user))
            blogService.setToken(user.token)
            //props.setUser(user)
            props.initBlogs()
            setTitle('')
            setAuthor('')
            setUrl('')
        }
    }, [props])

    const handleLogout = () => {
        try{
            //props.removeUser()
            window.localStorage.removeItem('loggedBlogappUser')
            props.handleNotification('Logout successfully, 5000')
        }catch(exception){
            props.handleError('Logout failed!', 5000)
        }
    }

    if(user === null){
        return(
            <LogIn />
        )
    }else{
        return(
            <div>
                <div>
                    {username.value} is logged in
                    <button onClick={handleLogout}>logout</button>
                </div>
                <Router>
                    <Route exact path='/' render={() => <Home />} />
                    <Route path='/users' render={() => <UserInformation /> } />
                </Router>
            </div>
        )
    }
}

const LogIn = () => {

    const handleLogin = async event => {
        event.preventDefault()
        try{
            const newUser = await loginService.login({ username: username.value, password: password.value })
            //await props.setUser(username, password)
            setUser(newUser)
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(newUser))
            blogService.setToken(newUser.token)
            props.handleNotification('Login successfull!', 5000)
            resetusername()
            resetpasswd()

        }catch(exception){
            props.handleError('Login failed!', 5000)
            resetusername()
            resetpasswd()
        }

    }

    return(
        <div>
            <Notification />
            <Error />
            <LoginForm
                username={username}
                password={password}
                handleSubmit={handleLogin}
            />
        </div>
    )
}



const mapStateToProps = (state) => {
    return {
        blogs: state.blogs,
        notification: state.notification,
        error: state.error,
        user: state.user,
    }
}

const mapDispatchToProps = {
    initBlogs,
    addNewBlog,
    likeBlog,
    removeBlog,
    handleNotification,
    handleError,
    setUser,
    removeUser,
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
