import React from 'react'
import LoginForm from './LoginForm'
import loginService from '../services/login'
import { connect } from 'react-redux'
import blogService from '../services/blogs'
import { handleNotification } from '../reducers/notificationReducer'
import { handleError } from '../reducers/errorReducer'


const Login = (props) => {

    const handleLogin = async event => {
        event.preventDefault()
        console.log('loggin in with: ', props.username.value, props.password.value)
        try{
            const newUser = await loginService.login({ username: props.username.value, password: props.password.value })
            //await props.setUser(username, password)

            props.handleUser(newUser)
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(newUser))
            blogService.setToken(newUser.token)
            props.handleNotification('Login successfull!', 5000)
            props.username.resetusername()
            props.password.resetpasswd()

        }catch(exception){
            props.handleError('Login failed!', 5000)
            //props.username.resetusername()
            props.password.resetpasswd()
        }

    }

    return(
        <div>
            <LoginForm
                username={props.username}
                password={props.password}
                handleSubmit={handleLogin}
            />
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        notification: state.notification,
        error: state.error,
    }
}

const mapDispatchToProps = {

    handleNotification,
    handleError

}

export default connect(mapStateToProps, mapDispatchToProps)(Login)
