        const blogs = blogService.getAll()
        dispatch({
            type: 'INITBLOGS',
            data: blogs
        })
    }
}


export const likeBlog = () => {

}



const blogReducer = (state = [], action) => {

    console.log(state)
    console.log(action)

    switch(action.type){

    case 'NEWBLOG':
        return state

    case 'INITBLOGS':
        return action.data

    case 'LIKEBLOG':
        return state
    default:
        return state
    }
}


export default blogReducer
