const setError = (message) => {
    return ({
        type: 'NOTIFICAITON',
        data: message
    })
}


const removeError = () => {
    return ({
        type: 'REMOVE',
        data: ''
    })
}


export const handleNotification = (message, time) => {
    return async dispatch => {
        dispatch(setError(message))
        setTimeout(() => {dispatch(removeError())}, time)
    }
}


const errorReducer = (state = null, action) => {

    switch(action.type){
    case 'ERROR':
        return action.data
    case 'REMOVE':
        return ''

    default:
        return state
    }
}


export default errorReducer
