/* eslint-disable no-unused-labels */
import React, { useState, useEffect } from 'react'
import Notification from './components/Notification'
import Error from './components/Error'
import { useUsername, usePassword } from './hooks'
import { initBlogs, addNewBlog, likeBlog, removeBlog } from './reducers/blogReducer'
import { handleNotification } from './reducers/notificationReducer'
import { setUser, removeUser } from './reducers/userReducer'
import { handleError } from './reducers/errorReducer'
import { getAllUsers } from './reducers/usersReducer'
import { connect } from 'react-redux'
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from 'react-router-dom'
import UserInformation from './components/UserInformation'
import Home from './components/Home'
import Login from './components/Login'
import blogService from './services/blogs'
import { Navbar, Button } from 'react-bootstrap'

const App = ( props ) => {

    const { resetpasswd, ...password } = usePassword('password')
    const { resetusername, ...username } = useUsername('username')
    const [user, setUser] = useState(null)

    const padding = { padding: 5 }

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
        if (loggedUserJSON) {
            const user = JSON.parse(loggedUserJSON)
            setUser(user)
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user))
            blogService.setToken(user.token)
            props.getAllUsers()
            //props.setUser(user)s
            props.initBlogs()
        }
    }, [])

    const handleLogout = () => {
        try{
            //props.removeUser()
            setUser(null)
            window.localStorage.removeItem('loggedBlogappUser')
            props.handleNotification('Logout successfully, 5000')
        }catch(exception){
            props.handleError('Logout failed!', 5000)
        }
    }

    if(user === null){
        return(
            <div>
                <Notification />
                <Error />
                <Router>
                    <Route exact path='/' render={() =>
                        <Login
                            username={username}
                            password={password}
                            resetpasswd={() => resetpasswd()}
                            resetusername={() => resetusername()}
                            handleUser={() => setUser()}
                        /> } />
                </Router>
            </div>
        )
    }else{
        return(
            <div className='container'>
                <Notification />
                <Error />
                <Router>
                    <Navbar bg="light" expand="lg">
                        <Navbar.Brand href="#home">Blog App</Navbar.Brand>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                            <Nav className="mr-auto">
                                <Nav.Link href="/">Home</Nav.Link>
                                <Nav.Link href="/users">Users</Nav.Link>
                            </Nav>
                        </Navbar.Collapse>
                    </Navbar>
                    <div>
                        <Link style={padding} to="/">home</Link>
                        <Link style={padding} to="/users">users</Link>
                        {username.value} is logged in
                        <Button onClick={handleLogout}>logout</Button>
                    </div>
                    <h1>Blog APP</h1>
                    <Route exact path='/' render={() => <Home user={user}/>} />
                    <Route path='/users' render={() => <UserInformation /> } />
                </Router>
            </div>
        )
    }
}




const mapStateToProps = (state) => {
    return {
        blogs: state.blogs,
        notification: state.notification,
        error: state.error,
        user: state.user,
        users: state.users,
    }
}

const mapDispatchToProps = {
    initBlogs,
    addNewBlog,
    likeBlog,
    removeBlog,
    handleNotification,
    handleError,
    setUser,
    removeUser,
    getAllUsers,
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
