import React, { useState, useEffect } from 'react'
import loginService from './services/login'
import Blog from './components/Blog'
import blogService from './services/blogs'
import Notification from './components/Notification'
import Error from './components/Error'
import LoginForm from './components/LoginForm'
import AddBlogForm from './components/AddBlogForm'
import Togglable from './components/Togglable'


const App = () => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [user, setUser] = useState(null)
    const [blogs, setBlogs] = useState([])
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')
    const [url, setUrl] = useState('')
    const [notification, setNotification] = useState('')
    const [error, setError] = useState('')

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
            if (loggedUserJSON) {
                const user = JSON.parse(loggedUserJSON)
                setUser(user)
                blogService.setToken(user.token)
                getBlogs()
            }
    }, [])


    const getBlogs = async () => {
        const allBlogs = await blogService.getAll()
        setBlogs(allBlogs.sort((a,b) =>  b.likes-a.likes ))
    }

    const handleLogout = () => {
        try{
            setUser(null)
            window.localStorage.removeItem('loggedBlogappUser')
            setNotification(`Logout successfully`)
            setTimeout(() => { setNotification('')}, 5000)
        }catch(exception){
            setError('Logout failed!')
            setTimeout(() => { setError('')}, 5000)
        }
    }

    const handleLogin = async event => {
        event.preventDefault()
        console.log('loggin in with', username, password)
        try{
            const newUser = await loginService.login({username, password})
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(newUser)) 

            setNotification('Login successfull')
            setTimeout(() => { setNotification('')}, 5000)

            await getBlogs()
            setUser(newUser)

            blogService.setToken(newUser.token)

            setPassword('')
            setUsername('')

        }catch(exception){
            setError('Login failed!')
            setTimeout(() => { setError('')}, 5000)
            setPassword('')
            setUsername('')
        }

    }

    const handleAddBlog = async event => {
        event.preventDefault()
        console.log('adding blog', title, author, url)

        const newBlog = {
                        title: title,
                        author: author,
                        url: url,
                        user: user.id
        }
        console.log(newBlog)
        const response = await blogService.create(newBlog)
        setBlogs(blogs.concat(response))
        setNotification(`a new blog ${title} by ${author} added`)
        setTimeout(() => { setNotification('')}, 5000)
        setAuthor('')
        setTitle('')
        setUrl('')
    }

    const handleLikes = async (blog) => {
        blog.likes += 1
        const response = await blogService.update(blog)
        const someBlogs = blogs.filter(otherBlogs => otherBlogs.id !== blog.id)
        console.log(someBlogs)
        const allBlogs = someBlogs.concat(response)
        const sortedBlogs = allBlogs.sort((a,b) =>  b.likes-a.likes )
        setBlogs(sortedBlogs)
        setNotification(`you liked blog ${blog.title} from author ${blog.author}`)
        setTimeout(() => { setNotification('')}, 5000)          
    }


    const handleRemoval = async (blog) => {
        const result = window.confirm(`Do you really want to delete blog ${blog.title}?`);
        if(result){
            const response = await blogService.remove(blog)
            const otherBlogs = blogs.filter(otherBlogs => otherBlogs.id !== response.id)
            setBlogs(otherBlogs)
            setNotification(`blog ${blog.title} by ${blog.author} got deleted`)
            setTimeout(() => { setNotification('')}, 5000)
        }
    }

   if(user === null){
       return(
           <div>
               <Notification message={notification}/> 
               <Error message={error}/> 
               <LoginForm username={username}
                          password={password} 
                          handlePasswordChange={({target}) => setPassword(target.value)}
                          handleUsernameChange={({target}) => setUsername(target.value)}
                          handleSubmit={handleLogin}
                />
           </div>
       )
   }else{
       return(
            <div>
                <Notification message={notification} /> 
                <Error message={error}/> 

                <div>
                    {user.username} is logged in
                    <button onClick={handleLogout}>logout</button>
                </div>
                <div>
                    <Togglable buttonLabel='new Blog'>
                        <AddBlogForm 
                            author={author}
                            title={title} 
                            url={url} 
                            handleAuthorChange={({target}) => setAuthor(target.value)}
                            handleTitleChange={({target}) => setTitle(target.value)}
                            handleUrlChange={({target}) => setUrl(target.value)}
                            handleAddBlog={handleAddBlog}
                         />
                    </Togglable>
                </div>
                <div>
                    {blogs.map(blog => <Blog key={blog.id} 
                                             blog={blog} 
                                             handleRemoval={() => handleRemoval(blog)} 
                                             handleLikes={() => handleLikes(blog)} 
                                             user={user} /> )}
                </div>
            </div>
       )
   }
}


export default App
