/* eslint-disable no-unused-labels */
import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import Notification from './components/Notification'
import Error from './components/Error'
import LoginForm from './components/LoginForm'
import AddBlogForm from './components/AddBlogForm'
import Togglable from './components/Togglable'
import { useUsername, usePassword } from './hooks'
import { initBlogs, addNewBlog, likeBlog, removeBlog } from './reducers/blogReducer'
import { handleNotification } from './reducers/notificationReducer'
import { setUser, removeUser } from './reducers/userReducer'
import { handleError } from './reducers/errorReducer'
import { connect } from 'react-redux'

const App = ( props ) => {

    const { resetusername, ...username } = useUsername('username')
    const { resetpasswd, ...password } = usePassword('password')
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')
    const [url, setUrl] = useState('')

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
        if (loggedUserJSON) {
            const user = JSON.parse(loggedUserJSON)
            props.setUser(user)
            props.initBlogs()
            setTitle('')
            setAuthor('')
            setUrl('')
        }
    }, [props])


    const handleLogout = () => {
        try{
            props.removeUser()
            props.handleNotification('Logout successfully, 5000')
        }catch(exception){
            props.handleError('Logout failed!', 5000)
        }
    }

    const handleLogin = async event => {
        event.preventDefault()
        console.log('loggin in with', username, password)
        try{
            props.setUser(username, password)
            props.handleNotification('Login successfull!', 5000)
            resetusername()
            resetpasswd()

        }catch(exception){
            props.handleError('Login failed!', 5000)
            resetusername()
            resetpasswd()
        }

    }

    const handleAddBlog = async event => {
        event.preventDefault()
        console.log('adding blog', title, author, url)

        const newBlog = {
            title: title,
            author: author,
            url: url,
        }
        props.addNewBlog(newBlog)
        props.handleNotification(`a new blog ${title} by ${author} added`, 5000)
        setAuthor('')
        setTitle('')
        setUrl('')
        window.location.reload()

    }

    const handleLikes = async (blog) => {
        props.likeBlog(blog)
        props.handleNotification(`you liked blog ${blog.title} from author ${blog.author}`, 5000)
    }


    const handleRemoval = async (blog) => {
        const result = window.confirm(`Do you really want to delete blog ${blog.title}?`)
        if(result){
            props.removeBlog(blog)
            props.handleNotification(`blog ${blog.title} by ${blog.author} got deleted`, 5000)
        }
    }

    if(props.user === null){
        return(
            <div>
                <Notification />
                <Error />
                <LoginForm
                    username={username}
                    password={password}
                    handleSubmit={handleLogin}
                />
            </div>
        )
    }else{
        return(
            <div>
                <Notification  />
                <Error />

                <div>
                    {props.user.username} is logged in
                    <button onClick={handleLogout}>logout</button>
                </div>
                <div>
                    <Togglable buttonLabel='new Blog'>
                        <AddBlogForm
                            author={author}
                            title={title}
                            url={url}
                            handleAuthorChange={({ target }) => setAuthor(target.value)}
                            handleTitleChange={({ target }) => setTitle(target.value)}
                            handleUrlChange={({ target }) => setUrl(target.value)}
                            handleAddBlog={handleAddBlog}
                        />
                    </Togglable>
                </div>
                <ul>
                    {props.blogs.map(blog =>
                        <Blog
                            key={blog.id}
                            blog={blog}
                            handleRemoval={() => handleRemoval(blog)}
                            handleLikes={() => handleLikes(blog)}
                            user={props.user} /> )}
                </ul>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        blogs: state.blogs,
        notification: state.notification,
        error: state.error,
        user: state.user,
    }
}

const mapDispatchToProps = {
    initBlogs,
    addNewBlog,
    likeBlog,
    removeBlog,
    handleNotification,
    handleError,
    setUser,
    removeUser,
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
