import React from 'react'
import LoginForm from './components/LoginForm'
import loginService from './services/login'


const Login = ({ username, password, user, handleUser }) => {

    const handleLogin = async event => {
        event.preventDefault()
        try{
            const newUser = await loginService.login({ username: username.value, password: password.value })
            //await props.setUser(username, password)
            setUser(newUser)
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(newUser))
            blogService.setToken(newUser.token)
            props.handleNotification('Login successfull!', 5000)
            username.resetusername()
            password.resetpasswd()

        }catch(exception){
            props.handleError('Login failed!', 5000)
            username.resetusername()
            password.resetpasswd()
        }

    }

    return(
        <div>
            <LoginForm
                username={username}
                password={password}
                handleSubmit={handleLogin}
            />
        </div>
    )
}

export default Login
