import React from 'react'
import blogService from '../services/blogs'

const BlogInfo = ({blog, blogs, handleBlogs}) => { 

    const increaseLikes = async (blog) => {
        blog.likes += 1
        const response = await blogService.update(blog)
        const allBlogs = blogs.filter(otherBlogs => otherBlogs.id !== blog.id)
        handleBlogs(allBlogs.concat(response))

    }

    const blogStyle = {
        paddingTop: 10,
        paddingLeft: 2,
        border: 'solid',
        borderWidth: 1,
        marginBottom: 5
      }

    return (
    <div style={blogStyle}>
        <p>{blog.title}</p>
        <p>{blog.author}</p>
        <p>{blog.url}</p> 
        <p>{blog.likes} <button onClick={() => increaseLikes(blog)}>like</button></p>
    </div>
)}

export default BlogInfo
