import React from 'react'
import blogService from '../services/blogs'

const BlogInfo = ({blog, blogs, handleBlogs, user}) => { 

    console.log(user)
    console.log(blog)
    const hideWhenNoAuthorization = { display: user === blog.user ? '' : 'none' }

    const increaseLikes = async () => {
        blog.likes += 1
        const response = await blogService.update(blog)
        const someBlogs = blogs.filter(otherBlogs => otherBlogs.id !== response.id)
        const allBlogs = someBlogs.concat(response)
        const sortedBlogs = allBlogs.sort((a,b) =>  b.likes-a.likes )
        handleBlogs(sortedBlogs)
    }


    const handleRemoval = async () => {
        const result = window.confirm(`Do you really want to delete blog ${blog.title}?`);
        if(result){
            const response = await blogService.remove(blog)
            const otherBlogs = blogs.filter(otherBlogs => otherBlogs.id !== response.id)
            handleBlogs(otherBlogs)
        }
    }

    const blogStyle = {
        paddingTop: 10,
        paddingLeft: 2,
        border: 'solid',
        borderWidth: 1,
        marginBottom: 5
      }

    return (
    <div style={blogStyle}>
        <p>{blog.title}</p>
        <p>{blog.author}</p>
        <p>{blog.url}</p> 
        <p>{blog.likes} <button onClick={() => increaseLikes(blog)}>like</button></p>
        <div style={hideWhenNoAuthorization}>
            <button onClick={() => handleRemoval()}>remove</button>
        </div>
    </div>
)}

export default BlogInfo
