import React, { useState, useEffect } from 'react'
import loginService from './services/login'
import Blog from './components/Blog'
import blogService from './services/blogs'
import Notification from './components/Notification'
import Error from './components/Error'
import LoginForm from './components/LoginForm'
import AddBlogForm from './components/AddBlogForm'


const App = () => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [user, setUser] = useState(null)
    const [blogs, setBlogs] = useState([])
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')
    const [url, setUrl] = useState('')
    const [notification, setNotification] = useState('')
    const [error, setError] = useState('')
    const [visibleBlogForm, setVisibleBlogForm] = useState(false)

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
            if (loggedUserJSON) {
                const user = JSON.parse(loggedUserJSON)
                setUser(user)
            }
    }, [])


    const handleLogout = () => {

        setUser(null)
        window.localStorage.removeItem('loggedBlogappUser')
    }

    const handleLogin = async event => {
        event.preventDefault()
        console.log('loggin in with', username, password)
        try{
            const newUser = await loginService.login({username, password})
            window.localStorage.setItem('loggedBlogappUser', JSON.stringify(user)) 

            setNotification('Login successfull')
            setTimeout(() => { setNotification('')}, 5000)

            const allBlogs = await blogService.getAll()
            setUser(newUser)
            setBlogs(allBlogs)
            console.log(newUser)

            blogService.setToken(newUser.token)

            setPassword('')
            setUsername('')

        }catch(exception){
            setError('Login failed!')
            setTimeout(() => { setError('')}, 5000)
            setPassword('')
            setUsername('')
        }

    }

    const handleAddBlog = async event => {
        event.preventDefault()
        console.log('adding blog', title, author, url)

        const newBlog = {
                        title: title,
                        author: author,
                        url: url,
                        user: user
        }
        console.log(newBlog)
        const response = await blogService.create(newBlog)
        setBlogs(blogs.concat(response))
        setNotification(`a new blog ${title} by ${author} added`)
        setTimeout(() => { setNotification('')}, 5000)
        setAuthor('')
        setTitle('')
        setUrl('')
}


   const addBlogForm = () => {

        const hideWhenVisible = {display: visibleBlogForm ? 'none' : ''} 
        const showWhenVisible = {display: visibleBlogForm ? '' : 'none'}

        return(

            <div>
                <div style={hideWhenVisible}>
                    <button onClick={() => setVisibleBlogForm(true)}>show</button>
                </div>
                <div style={showWhenVisible}>
                    <AddBlogForm 
                            author={author}
                            title={title}
                            url={url}
                            handleTitleChange={({target}) => setTitle(target.value)}
                            handleUrlChange={({target}) => setUrl(target.value)}
                            handleAuthorChange={({target}) => setAuthor(target.value)}
                            handleAddBlog={handleAddBlog}
                    />
                    <button onClick={() => setVisibleBlogForm(false)}>hide</button>
                </div>
            </div>

        )

   }

   if(user === null){
       return(
           <div>
               <Notification message={notification}/> 
               <Error message={error}/> 
               <LoginForm username={username}
                          password={password} 
                          handlePasswordChange={({target}) => setPassword(target.value)}
                          handleUsernameChange={({target}) => setUsername(target.value)}
                          handleSubmit={handleLogin}
                />
           </div>
       )
   }else{
       return(
            <div>
                <Notification message={notification} /> 
                <Error message={error}/> 

                <div>
                    {user.username} is logged in
                    <button onClick={handleLogout}>logout</button>
                </div>
                <div>
                    <AddBlogForm 
                        author={author}
                        title={title}
                        url={url}
                        handleTitleChange={({target}) => setTitle(target.value)}
                        handleUrlChange={({target}) => setUrl(target.value)}
                        handleAuthorChange={({target}) => setAuthor(target.value)}
                        handleAddBlog={handleAddBlog}
                    />
                </div>
                <div>
                    {blogs.map(blog => <Blog key={blog.id} blog={blog} /> )}
                </div>
            </div>
       )
   }
}


export default App
